// Challenge variables
const flies = [
  "2;2:6:3",
  "9;1:5:3",
  "12;2:6:2",
  "7;4:8:2",
  "1;3:3:4",
  "6;1:7:6",
  "4;4:9:1",
  "11;1:8:0",
  "10;2:8:7",
  "13;1:9:6",
  "5;2:2:6",
  "3;2:3:9",
  "8;5:1:8",
];

function solveProblem(flies) {
  flies = flies.map((fly) => {
    let [id, taille, x, y] = fly.split(/[:;]/g).map(Number);
    return { id, taille, x, y };
  });

  let energy = 80;
  let [x, y] = [0, 0];
  while (true) {
    let { fly, dist } = flies
      .map((fly) => {
        return {
          fly: fly,
          dist: Math.sqrt(Math.pow(x - fly.x, 2) + Math.pow(y - fly.y, 2)),
        };
      })
      .sort((a, b) => a.dist - b.dist)
      .log()[0];
    cl(
      `Spider at ${x}:${y}, closest fly is ${fly.id} (${fly.x}:${fly.y}, ${dist} away)`
    );
    break;
  }
  return energy;
}

console.log(`Answer: '${solveProblem(flies)}'`);

